// An obscure failure case involving if/else nested inside a blockless for.

test('parse-if-else-failure', function () {
  var c = caterwaul.clone('std');
  var f = c(function (x) {
    if (x === 5) for (var i = 0; i < x; ++i) {if (i === 3) return i;
                                              else         x = x}
    else if (x === 3) return x + 10;
    else return x + 1;
  });

  eq(f(0), 1);
  eq(f(3), 13);
  eq(f(5), 3);
});
